shapes_embed:
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix qudt: <http://qudt.org/schema/qudt/> .
@prefix unit: <http://qudt.org/vocab/unit/> .
@prefix dcterms: <http://purl.org/dc/terms/> .
@prefix dcat: <http://www.w3.org/ns/dcat#> .
@prefix prov: <http://www.w3.org/ns/prov#> .
@prefix base: <https://w3id.org/h2kg/hydrogen-ontology#> .

    base:Shape_DatasetCore a sh:NodeShape ;
      sh:targetNode base:ExperimentDataset ;
      sh:property [ sh:path dcterms:title ; sh:minCount 1 ] ;
      sh:property [ sh:path dcat:downloadURL ; sh:minCount 1 ] ;
      sh:property [ sh:path dcterms:license ; sh:minCount 1 ] ;
      sh:property [ sh:path dcterms:format ; sh:minCount 1 ; sh:severity sh:Warning ] ;
      sh:property [ sh:path dcterms:creator ; sh:minCount 1 ; sh:severity sh:Warning ] ;
      sh:property [ sh:path dcterms:identifier ; sh:minCount 1 ; sh:severity sh:Info ] .

    base:Shape_DatasetDescriptorLink a sh:NodeShape ;
      sh:targetNode base:DatasetDescriptor ;
      sh:property [
        sh:path base:isPartOf ;
        sh:hasValue base:ExperimentDataset ;
        sh:minCount 1
      ] .
