shapes_embed:
@prefix sh: <http://www.w3.org/ns/shacl#> .
@prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .
@prefix xsd: <http://www.w3.org/2001/XMLSchema#> .
@prefix qudt: <http://qudt.org/schema/qudt/> .
@prefix unit: <http://qudt.org/vocab/unit/> .
@prefix dcterms: <http://purl.org/dc/terms/> .
@prefix dcat: <http://www.w3.org/ns/dcat#> .
@prefix prov: <http://www.w3.org/ns/prov#> .
@prefix base: <https://w3id.org/h2kg/hydrogen-ontology#> .

    base:Shape_EnvironmentLog a sh:NodeShape ;
      sh:targetNode base:EnvironmentLog ;
      sh:property [ sh:path base:isPartOf ; sh:hasValue base:ExperimentDataset ; sh:minCount 1 ] ;
      sh:property [ sh:path base:hasParameter ; sh:minCount 1 ; sh:severity sh:Warning ] .

    # Soft expectations (warnings) that a few key parameters are present
    base:Shape_EnvParam_T a sh:NodeShape ;
      sh:targetNode base:EnvironmentLog ;
      sh:property [ sh:path base:hasParameter ; sh:hasValue base:Temperature ; sh:minCount 1 ; sh:severity sh:Warning ] .

    base:Shape_EnvParam_P a sh:NodeShape ;
      sh:targetNode base:EnvironmentLog ;
      sh:property [ sh:path base:hasParameter ; sh:hasValue base:Pressure ; sh:minCount 1 ; sh:severity sh:Warning ] .

    base:Shape_EnvParam_RH a sh:NodeShape ;
      sh:targetNode base:EnvironmentLog ;
      sh:property [ sh:path base:hasParameter ; sh:hasValue base:RelativeHumidity ; sh:minCount 1 ; sh:severity sh:Warning ] .

    base:Shape_EnvParam_Flow a sh:NodeShape ;
      sh:targetNode base:EnvironmentLog ;
      sh:property [ sh:path base:hasParameter ; sh:hasValue base:GasFlowRate ; sh:minCount 1 ; sh:severity sh:Warning ] .
